package ru.nsu.fit.tropin.Operations;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import ru.nsu.fit.tropin.CalculatorController.CalculatorStack;

import java.util.ArrayList;
import java.util.EmptyStackException;

import static org.junit.jupiter.api.Assertions.*;

class MultiplicationTest {
    private CalculatorStack calculatorStack;
    private Multiplication multiplication;
    private ArrayList<String> list;

    @BeforeEach
    void setUp() {
        calculatorStack = new CalculatorStack();
        multiplication = new Multiplication();
        list = new ArrayList<>();
    }

    @Test
    void calculation() throws Exception {
        calculatorStack.pushValue("10");
        calculatorStack.pushValue("40");
        multiplication.calculation(calculatorStack, list);
        assertEquals(400, calculatorStack.popValue());
    }

    @Test
    void calculationInfinity() throws Exception {
        calculatorStack.pushValue("10");
        calculatorStack.pushValue("");
        multiplication.calculation(calculatorStack, list);
        assertEquals(Double.POSITIVE_INFINITY, calculatorStack.popValue());
    }

    @Test
    void calculationEmpty() {
        assertThrows(EmptyStackException.class, () -> multiplication.calculation(calculatorStack, list));
    }

    @Test
    void calculationNotEnoughArgs() {
        calculatorStack.pushValue("10");
        assertThrows(EmptyStackException.class, () -> multiplication.calculation(calculatorStack, list));
    }
}