package ru.nsu.fit.tropin.Operations;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import ru.nsu.fit.tropin.CalculatorController.CalculatorStack;
import ru.nsu.fit.tropin.Exceptions.OperationException;

import java.util.ArrayList;
import java.util.EmptyStackException;

import static org.junit.jupiter.api.Assertions.*;

class SqrtTest {
    private CalculatorStack calculatorStack;
    private Sqrt sqrt;
    private ArrayList<String> list;

    @BeforeEach
    void setUp() {
        calculatorStack = new CalculatorStack();
        sqrt = new Sqrt();
        list = new ArrayList<>();
    }

    @Test
    void calculation() throws Exception {
        calculatorStack.pushValue("100");
        sqrt.calculation(calculatorStack, list);
        assertEquals(10, calculatorStack.popValue());
    }

    @Test
    void calculationInfinity() throws Exception {
        calculatorStack.pushValue("");
        sqrt.calculation(calculatorStack, list);
        assertEquals(Double.POSITIVE_INFINITY, calculatorStack.popValue());
    }

    @Test
    void calculationEmpty() {
        assertThrows(EmptyStackException.class, () -> sqrt.calculation(calculatorStack, list));
    }

    @Test
    void calculationNegativeNumber() {
        calculatorStack.pushValue("-1");
        assertThrows(OperationException.class, () -> sqrt.calculation(calculatorStack, list));
    }
}